#include <ncurses.h>
#include <stdio.h>
#include <stdlib.h>
#include <locale.h>
#include "display.h"
#define BUF_LEN 1024
char buf[BUF_LEN];
char content[30];

char * initMap(){
    char * map = (char *)calloc(70, sizeof(char));
    int i = 0;
    map[i++] = 'S';
    for(int m = 0; m < 13; ++m)
        map[i++] = '0';
    map[i++] = 'H';
    for(int m = 0; m < 13; ++m)
        map[i++] = '0';
    map[i++] = 'T';
    for(int m = 0; m < 6; ++m)
        map[i++] = '0';
    map[i++] = 'G';
    for(int m = 0; m < 13; ++m)
        map[i++] = '0';
    map[i++] = 'P';
    for(int m = 0; m < 13; ++m)
        map[i++] = '0';
    map[i++] = 'M';
    for(int m = 0; m < 6; ++m)
        map[i++] = '$';
    return map;
}

int contain_string(char * content, char * str){
    if(!content || !str)
        return 0;
    int i;
    for(i = 0; content[i] != '\0' && str[i] != '\0'; ++i){
        if(str[i] != content[i])
            break;
    }
    if(content[i] != '\0')
        return 0;
    return i;
}

int main(void){
    init_display();
    char * map = initMap();
    display_map(29, 8, map);
    refresh();
    char tips[] = "";
    init_tips(tips, COLOR_P2);
    show_tips();
    char hint[] = "钱夫人>";
    char buf[1024];
    get_input(hint, COLOR_Q, buf, 10);
    endwin();
    exit(0);
}

